--单向队列
local Node = {item = 0,next = nil};
function Node:new(o)
    o = o or {}
    setmetatable(o,Node)
    return o;
end

local Queue = {first = nil,last = nil,N = 0}
Queue.__index = Queue;

function Queue:new(o)
    o = o or {}
    setmetatable(o,Queue)
    return o;
end
function Queue:isEmpty()
    return self.N == 0;
end

