end
--向表尾添加元素
function Queue:enqueue(item)
    local oldLast = self.last;
    self.last = Node.new();
    self.last.item = item;
    self.last.next = nil;
    if(self:isEmpty())then
        self.first = self.last;
    else
        oldLast.next = self.last;
    end
    self.N = self.N+1;
end
--从表头删除元素
function Queue:dequeue()
    local item = self.first.item;
    self.first = self.first.next;
    if(self:isEmpty())then
        self.last = nil;
    end
    self.N = self.N - 1;
    return item;
end

